    public static int count(int[] L, int[] R) {
		return count(L,R,0,Integer.MIN_VALUE) % 998244353;
	}
	
	// bruteforce solution, takes too much time for some inputs
	private static int count(int[] L, int[] R, int i, int c) {
		if(i >= L.length) {
			return 1;
		}
		int l = L[i];
		int r = R[i];
		c = Math.max(l,c+1);
		int res = 0;
		while(c <= r) {
			res += count(L,R,i+1,c++);
		}
		return res;
	}

